//Writing a program in Java to implement implicit and explicit type casting
public class explict_cast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//char var='a';
		//int vari=100;
		long varl=1233493495080L;
		float varf=1232.3f;
		double vard=12312342.4345342d;
		//System.out.println("from int "+vari+"to char is "+(char)var);
		System.out.println("from long "+varl+"to int is "+(int)varl);
		System.out.println("from float "+varf+" to int is "+(int)varf);
		System.out.println("from double "+vard+" to int  is "+(int)vard);
	
	}

}
